document.addEventListener('DOMContentLoaded', () => {
    //Ha a "vissza" gomb lett megnyomva hozzáad a formhoz egy hidden inputot "back" értékkel, majd elküldi
    $("#back").click(function() {
        $("<input/>").attr("type", "hidden")
            .attr("name", "switch")
            .attr("value", "back")
            .appendTo("#switchpage");
        $("#switchpage").submit();
    });

    //Ha az "előre" gomb lett megnyomva hozzáad a formhoz egy hidden inputot "forward" értékkel, majd elküldi
    $("#forward").click(function() {
        $("<input/>").attr("type", "hidden")
            .attr("name", "switch")
            .attr("value", "forward")
            .appendTo("#switchpage");
        $("#switchpage").submit();
    });
});